// Find Longest Word
// Complete the function 'findLongestWord' that takes an array of strings as an argument
// and returns the longest word in the array.

function findLongestWord(words) {
  let longestWord = '';

  // Your code here

  return longestWord;
}

solution:
function findLongestWord(words) {
  let longestWord = '';

  words.forEach((word) => {
    if (word.length > longestWord.length) {
      longestWord = word;
    }
  });

  return longestWord;
}

