// Capitalize Words
// Complete the function 'capitalizeWords' that takes an array of strings as an argument
// and returns a new array with the first letter of each string capitalized.

function capitalizeWords(words) {
  let capitalizedWords = [];

  // Your code here

  return capitalizedWords;
}

solution:
function capitalizeWords(words) {
  let capitalizedWords = [];

  words.forEach((word) => {
    capitalizedWords.push(word.charAt(0).toUpperCase() + word.slice(1));
  });

  return capitalizedWords;
}

