// Filter Even Numbers
// Complete the function 'filterEvenNumbers' that takes an array of numbers as an argument
// and returns a new array containing only the even numbers.

function filterEvenNumbers(numbers) {
  let evenNumbers = [];

  // Your code here

  return evenNumbers;
}

solution:
function filterEvenNumbers(numbers) {
  let evenNumbers = [];

  numbers.forEach((number) => {
    if (number % 2 === 0) {
      evenNumbers.push(number);
    }
  });

  return evenNumbers;
}
